function eyeMedia_NextPreviousDo(myPid,Keep_Playing,Check_Num,Current_Row){Current_Row.className="sort-table-select";var This_Table;eval("This_Table = table_"+myPid+"_eyeMedia_tblPlaylist;");This_Table.lastClick=Current_Row;if(This_Table.mySignal){sendMsg(This_Table.mychecknum,This_Table.mySignal,eyeParam(This_Table.realName,This_Table.getSelectValue(This_Table.myMaster)))}if(Keep_Playing){setTimeout("sendMsg("+Check_Num+",'tlbToolbar_Play')",500)}}function eyeMedia_Next(d,b,e){var a=document.getElementById(d+"_eyeMedia_tblPlaylist_Body").firstChild;while(a.className!="sort-table-select"){a=a.nextSibling}a.className="";if(document.getElementById(d+"_tlbToolbar_Shuffle_Container").className=="blockbarItemPress"){var c=Math.floor(Math.random()*(a.parentNode.childNodes.length));a=a.parentNode.childNodes[c];eyeMedia_NextPreviousDo(d,b,e,a)}else{if(!(a.nextSibling)){if(document.getElementById(d+"_tlbToolbar_Repeat_Container").className=="blockbarItemPress"){a=a.parentNode.firstChild;eyeMedia_NextPreviousDo(d,b,e,a)}}else{a=a.nextSibling;eyeMedia_NextPreviousDo(d,b,e,a)}}}function eyeMedia_Previous(c,b,d){var a=document.getElementById(c+"_eyeMedia_tblPlaylist_Body").firstChild;while(a.className!="sort-table-select"){a=a.nextSibling}a.className="";if(!(a.nextSibling)){if(document.getElementById(c+"_tlbToolbar_Repeat_Container").className=="blockbarItemPress"){a=a.parentNode.lastChild;eyeMedia_NextPreviousDo(c,b,d,a)}}else{a=a.previousSibling;eyeMedia_NextPreviousDo(c,b,d,a)}}function eyeMedia_SelectFirst(c,b,d){var a=document.getElementById(c+"_eyeMedia_tblPlaylist_Body").firstChild;eyeMedia_NextPreviousDo(c,b,d,a);while(a.className!="sort-table-select"){a=a.nextSibling}if(a.rowIndex!=1){a.className=""}if(b){setTimeout("sendMsg("+d+",'tlbToolbar_Play')",500)}};