function init(){SXE.initElementDialog("del");if(SXE.currentAction=="update"){setFormValue("datetime",tinyMCEPopup.editor.dom.getAttrib(SXE.updateElement,"datetime"));setFormValue("cite",tinyMCEPopup.editor.dom.getAttrib(SXE.updateElement,"cite"));SXE.showRemoveButton()}}function setElementAttribs(a){setAllCommonAttribs(a);setAttrib(a,"datetime");setAttrib(a,"cite")}
function insertDel(){var a=tinyMCEPopup.editor.dom.getParent(SXE.focusElement,"DEL");tinyMCEPopup.execCommand("mceBeginUndoLevel");if(a==null){if(SXE.inst.selection.getContent().length>0){insertInlineElement("del");for(var d=tinymce.grep(SXE.inst.dom.select("del"),function(c){return c.id=="#sxe_temp_del#"}),b=0;b<d.length;b++){a=d[b];setElementAttribs(a)}}}else setElementAttribs(a);tinyMCEPopup.editor.nodeChanged();tinyMCEPopup.execCommand("mceEndUndoLevel");tinyMCEPopup.close()}
function insertInlineElement(a){var d=tinyMCEPopup.editor,b=d.dom;d.getDoc().execCommand("FontName",false,"mceinline");tinymce.each(b.select(tinymce.isWebKit?"span":"font"),function(c){if(c.style.fontFamily=="mceinline"||c.face=="mceinline")b.replace(b.create(a),c,1)})}function removeDel(){SXE.removeElement("del");tinyMCEPopup.close()}tinyMCEPopup.onInit.add(init);
